import React, {useState} from "react";
import {ContactPicker} from '../contactPicker/ContactPicker';

const getTodayString = () => {

  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  name,
  setName,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  const [selectedContact, setSelectedContact] = useState({});

  const handleChange = (event) => {
    const selectedName = event.target.value;
    const chosenContact = contacts.filter(
      (contact) => contact.name === selectedName
    );
    setSelectedContact(chosenContact);
    console.log('This is the contact selected in the form : ', chosenContact);
    console.log('This is the contact set to selectedContact in the form : ', selectedContact);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Title:</label>
        <input type='text' id='name' value={name} onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label htmlFor="date">Date:</label>
        <input type='date' min={getTodayString()} id='date' value={date} onChange={(e) => setDate(e.target.value)} />
      </div>
      <div>
        <label htmlFor="time">Time:</label>
        <input type='time' id='time' value={time} onChange={(e) => setTime(e.target.value)} />
      </div>
      <ContactPicker contacts={contacts} handleChange={handleChange} value={selectedContact} name={name} />
      <button type='submit'>Add Appointment</button>
    </form>
  );
};
